import React, { useEffect, useState } from 'react'
import { MDBAccordion, MDBAccordionItem } from 'mdb-react-ui-kit';

const ManageJobs = () => {

    const [currentUser, setCurrentUser] = useState(JSON.parse(localStorage.getItem('user')))
    console.log(currentUser);

    const [jobData, setJobData] = useState([])
    const [compData, setCompData] = useState([])
    const [loading, setLoading] = useState(false)

    const fetchCompData = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/profile/getbyuser/' + currentUser._id)
        setLoading(false);
        console.log(res.status)
        // console.log(currentUser._id)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setCompData(data)
        }
    }

    const fetchJobData = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/add_jobs/getbyuser/' + currentUser._id)
        setLoading(false);
        console.log(res.status)
        // console.log(currentUser._id)

        if (res.status === 200) {
            const data = await res.json();
            console.log(data)
            setJobData(data)
        }
    }



    useEffect(() => {
        fetchCompData();
        fetchJobData();
    }, [])



    return (

        <div>
            <div className="card container">
                <div className="card-body">
                    <h5 className="card-title">Card title</h5>
                    <MDBAccordion>
                        <MDBAccordionItem name='' headerTitle='Accordion Item #1'>
                            Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus terry richardson ad squid. 3 wolf
                            moon officia aute, non cupidatat skateboard dolor brunch. Food truck quinoa nesciunt laborum eiusmod.
                            Brunch 3 wolf moon tempor, sunt aliqua put a bird on it squid single-origin coffee nulla assumenda
                            shoreditch et. Nihil anim keffiyeh helvetica, craft beer labore wes anderson cred nesciunt sapiente ea
                            proident. Ad vegan excepteur butcher vice lomo. Leggings occaecat craft beer farm-to-table, raw denim
                            aesthetic synth nesciunt you probably haven't heard of them accusamus labore sustainable VHS.
                        </MDBAccordionItem>
                    </MDBAccordion>
                </div>
            </div>

        </div>
    )
}

export default ManageJobs
